import 'regenerator-runtime/runtime';

import chai from 'chai';
import chaiExclude from 'chai-exclude';
chai.use(chaiExclude);

import { MongoClient } from 'mongodb';

const DB_NAME = process.env.NODE_ENV === 'test' ? 'TEST_DB' : 'PROD_DB';

export const getUserByUsername = async username => {
  const client = await MongoClient.connect(
    `mongodb://localhost:27017/${DB_NAME}`
  );
  const db = client.db(DB_NAME);

  const result = await db.collection('users').findOne({ username });

  client.close();

  return result;
};

export default {
  getUserByUsername,
};

import { MongoClient } from 'mongodb';

const DB_NAME = process.env.NODE_ENV === 'test' ? 'TEST_DB' : 'PROD_DB';

export const setDatabaseData = async (collectionName, data) => {
  const client = await MongoClient.connect(
    `mongodb://localhost:27017/${DB_NAME}`
  );
  const db = client.db(DB_NAME);

  await db.collection(collectionName).insertMany(data);

  client.close();
};

export const getDatabaseData = async collectionName => {
  const client = await MongoClient.connect(
    `mongodb://localhost:27017/${DB_NAME}`
  );
  const db = client.db(DB_NAME);

  const result = await db.collection(collectionName).find().toArray();

  client.close();

  return result;
};

export const resetDatabase = async () => {
  const client = await MongoClient.connect(
    `mongodb://localhost:27017/${DB_NAME}`
  );
  const db = client.db(DB_NAME);

  await db.dropDatabase();

  client.close();
};

import express from 'express';
import db from './db';

const app = express();

app.get('/users/:username', async (req, res) => {
  const { username } = req.params;

  try {
    const user = await db.getUserByUsername(username);

    if (user) {
      res.json(user);
    } else {
      res.status(404).send();
    }
  } catch (error) {
    res.status(500).json(error);
  }
});

export { app };

import { isEqual } from 'lodash';
import { getLetterCount } from './letter-count';

export const isAnagram = (string1, string2) => {
  const firstWordLetters = getLetterCount(
    string1.toLowerCase().replace(/\s/g, '')
  );
  const secondWordLetters = getLetterCount(
    string2.toLowerCase().replace(/\s/g, '')
  );

  return isEqual(firstWordLetters, secondWordLetters);
};

// 'cat' -> { c: 1, a: 1, t: 1 }
// 'better' -> { b: 1, e: 2, t: 2, r: 1 }

export const getLetterCount = string => {
  return string.split('').reduce(
    (acc, letter) => ({
      ...acc,
      [letter]: acc[letter] ? acc[letter] + 1 : 1,
    }),
    {}
  );
};
